<template>
    <div class="box" :class="{ center }">
        <slot name="header"></slot>
        <div class="clock" :style="[{
            transform: `translate(${offsetX}px,${offsetY}px)`,
            '-webkit-transform': `translate(${offsetX}px,${offsetY}px)`,
            '-moz-transform': `translate(${offsetX}px,${offsetY}px)`
        }, clockSize, clockTheme]">
            <div class="hour" :style="{
                transform: `rotate(${hh}deg)`
            }"></div>
            <div class="min" :style="{
                transform: `rotate(${ii}deg)`
            }"></div>
            <div class="sec" :style="{
                transform: `rotate(${ss}deg)`
            }"></div>
        </div>
        <slot name="footer">
        </slot>
    </div>
</template>

<script>
export default {
    name: "CircleClock",
    props: {
        size: {
            type: String | Number,
            default: 'fit',
            validator(val) {
                return val.trim()
            }
        },
        center: {
            type: Boolean,
            default: false,
        },
        offsetX: {
            type: Number,
            default: 0,
        },
        offsetY: {
            type: Number,
            default: 0,
        },
        theme: {
            type: String,
            default: 'auto',
            validator(val) {
                return val.trim()
            }
        },
        GMT: {
            type: Number,
            default: -new Date().getTimezoneOffset() / 60,
            validator(val) {
                return val <= 12 && val >= -12
            }
        },
        change: {
            type: Boolean,
            default: false,
        },
        step: {
            type: Boolean,
            default: true,
        }
    },
    data() {
        return {
            currentTheme: localStorage.getItem('theme') ?? 'light',
            date: this.step ? new Date() : Date.now(),
            timer: null
        }
    },
    computed: {
        hh() {
            return ((new Date(this.date)).getTime() % (1000 * 60 * 60 * 24) / (1000 * 60 * 60) - new Date().getTimezoneOffset() / 60) * (360 / 12)
        },
        ii() {
            return this.step ? this.date.getMinutes() * 6 : this.date % (1000 * 60 * 60) / (1000 * 60) * (360 / 60)
        },
        ss() {
            return this.step ? this.date.getSeconds() * 6 : this.date % (1000 * 60) / 1000 * (360 / 60)
        },
        clockSize() {
            const style = {}
            switch (this.size) {
                case 'large':
                    style['--size'] = `400px`
                    break
                case 'middle':
                    style['--size'] = `200px`
                    break
                case 'small':
                    style['--size'] = `100px`
                    break
                case 'fit':
                    style['--size'] = `50vmin`
                    break
                default:
                    if (this.size.includes('%'))
                        style['--size'] = `${this.size}`
                    else
                        style['--size'] = `${this.size}px`
            }
            return style
        },
        clockTheme() {
            const style = {}
            switch (this.theme) {
                case 'dark':
                    style['--border-color'] = '#111'
                    style['--text-color'] = '#888'
                    break
                case 'light':
                    style['--border-color'] = '#fff'
                    style['--text-color'] = '#333'
            }
            return style
        },
        offset() {
            return new Date().getTimezoneOffset() / 60 + this.GMT
        }
    },
    methods: {
        getDate() {
            this.date = this.step ? new Date() : Date.now()
        },
    },
    mounted() {
        this.getDate()
        this.timer = setInterval(this.getDate, this.step ? 1000 : 1000 / (360 / 6));
    },
    beforeDestroy() {
        clearInterval(this.timer)
        this.timer = null
    },
}
</script>

<style scoped>
.box {
    margin: 0;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    position: relative;
    transition: all ease 0.2s;
    --text-color: #333;
    --border-color: #fff;
}

.box.center {
    inset: 0;
    position: absolute;
    margin: auto;
    height: fit-content;
}

[data-theme="dark"] .box {
    --text-color: #888;
    --border-color: #111;
}

.clock {
    height: var(--size);
    width: var(--size);
    display: flex;
    justify-content: center;
    align-items: center;
    background-image: url("data:image/png;base64,");
    background-position: center center;
    background-size: cover;
    border-radius: 50%;
    border: calc(var(--size)/50) solid var(--border-color);
    box-shadow: 0 -15px 15px rgba(255, 255, 255, 0.05),
        inset 0 -15px 15px rgba(255, 255, 255, 0.05), 0 15px 15px rgba(0, 0, 0, 0.3),
        inset 0 15px 15px rgba(0, 0, 0, 0.3);
}

.clock:before {
    content: "";
    height: calc(var(--size) * 0.04);
    width: calc(var(--size) * 0.04);
    background-color: var(--text-color);
    border: calc(var(--size) * 0.01) solid var(--border-color);
    position: absolute;
    border-radius: 50%;
    z-index: 1000;
    transition: all ease 0.2s;
}

.hour,
.min,
.sec {
    position: absolute;
    display: flex;
    justify-content: center;
    border-radius: 50%;
}

.hour {
    height: calc(var(--size) / 2);
    width: calc(var(--size) / 2);
}

.hour:before {
    content: "";
    position: absolute;
    height: 50%;
    width: calc(var(--size) / 50);
    background-color: var(--text-color);
    border-radius: calc(var(--size) / 50);
}

.min {
    height: calc(var(--size) / 1.75);
    width: calc(var(--size) / 1.75);
}

.min:before {
    content: "";
    height: 50%;
    width: calc(var(--size) / 100);
    background-color: var(--text-color);
    border-radius: calc(var(--size) / 100);
}

.sec {
    height: calc(var(--size) / 1.5);
    width: calc(var(--size) / 1.5);
}

.sec:before {
    content: "";
    height: 60%;
    width: calc(var(--size) / 200);
    background-color: #f00;
    border-radius: calc(var(--size) / 200);
}

.switch-cont {
    margin: 2em auto;
    bottom: 0;
}

.switch-cont .switch-btn {
    font-family: monospace;
    text-transform: uppercase;
    padding: 0.5rem 1rem;
    cursor: pointer;
    transition: all ease 0.3s;
}
</style>